import React from 'react';
import styles from './posts.module.css'


const Posts = (props) => {
    let updatePost = (e) =>{    
        let text = e.target.value;
        props.updatePost(text);
    }
    let sendPost = () =>{
        props.sendPost();
    }
    return <div className={styles.postsContainer}>
        <textarea name="" id="" cols="30" rows="10" onChange={updatePost} value={props.currentTextPost}></textarea>
        <button disabled={!(props.currentTextPost)} onClick={sendPost} className={styles.buttonPost}>Post</button>
        {props.posts.map((p) => <div key={p.id} className={styles.itemPost}>
        {p.text}
        <span className={styles.likes}>likes: {p.likesCount}</span>
        </div>)}
    </div>
}

export default Posts;
