import React from 'react';
import styles from './Friends.module.css'
import avatar from '../../assets/img/ava.jpg'
import { NavLink } from 'react-router-dom'
import {usersAPI} from '../../API/api';


let FriendsFunc = (props) => {
    let pageCount = Math.ceil(props.totalUsersCout / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pageCount; i++) {
        pages.push(i);
    }
    return <div className={styles.hz}>
        <div>
            {pages.map(p => {
                return <span className={props.currentPage === p && styles.selectedPage}
                    onClick={(e) => props.onPageChanged(p)}
                >{p}</span>

            })}
        </div>

        {
            props.users.map(u => <div key={u.id}>
                <span>
                    <div>
                        <NavLink to={'/profile/' + u.id}>
                            <img src={u.photos.small != null ? u.photos.small : avatar} alt="" className={styles.usersAvatar} />
                        </NavLink>
                    </div>
                    <div>
                        {u.followed
                            ? <button onClick={() => {
                                props.toggleFollowingProgress(u.id);
                                usersAPI.deleteFollow(u.id).then(data => {
                                    if (data.resultCode === 0) {
                                        props.unfollow(u.id);
                                        props.toggleFollowingProgress(u.id);
                                    }
                                })
                            }} disabled={props.followingInProgress.some(u.id)}>Unfollow</button>
                            : <button onClick={() => {
                                props.toggleFollowingProgress(u.id);
                                usersAPI.postFollow(u.id).then(data => {
                                    if (data.resultCode === 0) {
                                        props.follow(u.id);
                                        props.toggleFollowingProgress(u.id);
                                    }
                                })
                            }} disabled={props.followingInProgress.some(u.id)}>Follow</button>
                        }
                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.name}</div>
                    </span>
                    <span>
                        <div>u.location.country</div>
                        <div>u.location.city</div>
                    </span>
                </span>
            </div>)
        }
    </div >
}

export default FriendsFunc;
