import React from 'react';
import axios from 'axios';
import Profile from './Profile'
import { connect } from 'react-redux';
import {setUserProfile,sendPost,updatePost} from './../../redux/profile-reducer'

class ProfileContainer extends React.Component{
    componentDidMount(){
        axios.get('https://social-network.samuraijs.com/api/1.0/profile/2').then(response => {
            this.props.setUserProfile(response.data);
        })
    }
    render(){
        return <Profile {...this.props}/>
    }

}
let WithProfileContainer = withRouter(ProfileContainer)
const mapStateToProps = (state) => ({
    ProfilePage: state.ProfilePage
})
export default connect(mapStateToProps,{setUserProfile,sendPost,updatePost})(ProfileContainer);
