import React, { useState, useEffect, createRef } from 'react';

import styles from './profileStatus.module.css';


const ProfileStatusWithHooks = ({ isMyPage, ...props }) => {
    const [editMode, setEditMode] = useState(false);
    const [status, setStatus] = useState(props.status);

    let node = ( createRef(null));

    const handeClick = (e) =>{
        if(e.target !== node.current){
            setEditMode(false);
            document.removeEventListener('click', handeClick)
        }    
    }
    useEffect(() => {
        if(node){
            document.addEventListener('click', handeClick)
        }
        return document.removeEventListener('click', handeClick);
    }, [node])
    useEffect(() => {
        setStatus(props.status);
    }, [props.status])
   
    const outEditMode = (e) => {
        console.log(e.target)
        setEditMode(false);
        setStatus(props.status);
    
    }



    return <div>
        {isMyPage ?
            <div className={styles.statusContainer} >
                <div>
                    <span onClick={()=>{setEditMode(true)}} className={styles.status}>{props.status || 'set a status message'}</span>
                </div>
                {editMode && <div className={styles.changeStatusContainer} ref={node}>
                    <div ><input onBlur={outEditMode} className={styles.inputStatus} autoFocus={true} onChange={(e) => { setStatus(e.currentTarget.value) }} value={status} /></div>
                    <button className={styles.saveButton} onClick={()=>{setStatus(status)}}>Save</button>
                </div>}
            </div>
            : <div>
                {
                    props.status
                        ? props.status.length > 100 ? props.status.slice(0, 100) : props.status
                        : null
                }
            </div>
        }

    </div>

}

export default ProfileStatusWithHooks;
