import { authAPI } from '../API/api';
import { stopSubmit } from 'redux-form';

const SET_USER = 'SET_USER';
const RESET_USER = 'RESET_USER';

let initialState = {
    id: null,
    email: null,
    login: null,
    isAuth: false
}

const authReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_USER:
            debugger;
            return {

                ...state,
                ...action.data,
                isAuth: true
            }
        case RESET_USER:
            return {
                initialState
            }
        default: return state;

    }

}

export const setUserAuth = (data) => ({ type: SET_USER, data });
export const resetUserAuth = () => ({ type: RESET_USER });

export const getAuthThunk = () => (dispatch) => {
    debugger;
    return authAPI.getAuth().then(data => {
        
        if (data.resultCode === 0) {
            
            dispatch(setUserAuth(data.data));
        }
    })
}
export const logInThunk = (dataForm) => (dispatch) => {
    authAPI.logIn(dataForm).then(data => {
        if (data.resultCode === 0) {
            dispatch(getAuthThunk(data.data));
        } else {
            let messages = data.messages.length ? data.messages[0] : 'Some error';
            dispatch(stopSubmit('login', { _error: messages }))
        }
    })
}
export const logOutThunk = () => (dispatch) => {
    authAPI.logOut().then(data => {
        if (data.resultCode === 0) {
            dispatch(resetUserAuth());
        }
    })
}

export default authReducer;
