import React from 'react';
import ProfileStatusWithHooks from './ProfileStatusWithHooks';
import { reduxForm, Field } from 'redux-form';
import { createField, Input, Textarea } from '../../common/FormControls/FormControls';
import { required } from '../../../utils/validators/validator';
import { Prompt } from 'react-router'
import SideBar from '../../SideBar/SideBar';


const ProfileDescrition = ({ setEditProfile, isEditMode, profile, isMyPage, ...props }) => {


    const onSubmit = (formData) => {
        props.saveProfileData(formData);
    }
    return <div>

        <h2>{profile.fullName}</h2>
        <ProfileStatusWithHooks isMyPage={isMyPage} updateStatus={props.updateStatus} status={props.status} />

        {profile.aboutMe && <div>Description: {profile.aboutMe} </div>}
        <div>Looking for a job: {profile.lookingForAJob ? 'yes' : 'no'}</div>
        {profile.lookingForAJobDescription && <div>Description: {profile.lookingForAJobDescription} </div>}
        {!isEditMode && Object.keys(profile.contacts).map((keys) => {
            if (profile.contacts[keys]) {
                return <div>{`${keys} : ${profile.contacts[keys]}`}</div>
            }
        })}
        {isMyPage && !isEditMode && <button onClick={() => { setEditProfile(true) }}>Edit</button>}
        {isMyPage && isEditMode && <ProfileEditorReduxForm isEditMode={isEditMode} initialValues={profile} onSubmit={onSubmit} profile={profile} />}
    </div>
}

const ProfileEditor = ({ handleSubmit, profile }) => {
    debugger;
    return <form onSubmit={handleSubmit}>
       
        <h3>Full name</h3>
        {createField('Full Name', 'fullName', [required], Input, false, null)}
        <label>Looking for a Job: </label>
        <div>
            <label><Field name="lookingForAJob" component={'input'} type="radio" value='true' /> Yes</label>
            <label><Field name="lookingForAJob" component={'input'} type="radio" value='false' checked={true} /> No</label>
        </div>
        <h3>Skills:</h3>
        {createField(null, 'lookingForAJobDescription', [required], Textarea, false, null, null, { colls: 10, rows: 30 })}
        <h3>About me:</h3>
        {createField(null, 'aboutMe', [required], Textarea, false, null, null, { colls: 10, rows: 30 })}
        <h2>Contacts:</h2>
        {Object.keys(profile.contacts).map((key) => {
            return <div>
                <h3>
                    {key}
                </h3>
                {createField(key, 'contacts.' + key, null, Input, false, null, { value: profile.contacts[key] })}
            </div>
        })
        }
        <Prompt
            when={SideBar}
            message='You have unsaved changes, are you sure you want to leave?'
        />
        <button>save</button>
    </form>
}
const ProfileEditorReduxForm = reduxForm({ form: 'edit-profile' })(ProfileEditor);

export default ProfileDescrition;
