const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS';

let initialState = {
    users: [
        { id: 1, photourl:'http://images.nickjr.com/nickjr/properties/go-diego-go/go-diego-go-1x1.jpg', followed: true, fullName: 'Igor', location: { city: 'Lugansk', country: 'Ukraine' } },
        { id: 2, followed: true, fullName: 'Maxim', location: { city: 'Kiev', country: 'Ukraine' } },
        { id: 3, followed: true, fullName: 'Artur', location: { city: 'London', country: 'England' } },
        { id: 4, followed: true, fullName: 'Jan', location: { city: 'Paris', country: 'France' } },
        { id: 5, followed: true, fullName: 'Igor', location: { city: 'Lugansk', country: 'Ukraine' } },
        { id: 6, followed: true, fullName: 'Maxim', location: { city: 'Kiev', country: 'Ukraine' } },
        { id: 7, followed: true, fullName: 'Artur', location: { city: 'London', country: 'England' } },
        { id: 8, followed: true, fullName: 'Jan', location: { city: 'Paris', country: 'France' } },
        { id: 9, followed: true, fullName: 'Igor', location: { city: 'Lugansk', country: 'Ukraine' } },
        { id: 10, followed: true, fullName: 'Maxim', location: { city: 'Kiev', country: 'Ukraine' } },
        { id: 11, followed: true, fullName: 'Artur', location: { city: 'London', country: 'England' } },
        { id: 12, followed: true, fullName: 'Jan', location: { city: 'Paris', country: 'France' } },
        { id: 13, followed: true, fullName: 'Igor', location: { city: 'Lugansk', country: 'Ukraine' } },
        { id: 14, followed: true, fullName: 'Maxim', location: { city: 'Kiev', country: 'Ukraine' } },
        { id: 15, followed: true, fullName: 'Artur', location: { city: 'London', country: 'England' } },
        { id: 16, followed: true, fullName: 'Jan', location: { city: 'Paris', country: 'France' } },
        { id: 17, followed: true, fullName: 'Igor', location: { city: 'Lugansk', country: 'Ukraine' } },
        { id: 18, followed: true, fullName: 'Maxim', location: { city: 'Kiev', country: 'Ukraine' } },
        { id: 19, followed: true, fullName: 'Artur', location: { city: 'London', country: 'England' } },
        { id: 20, followed: true, fullName: 'Jan', location: { city: 'Paris', country: 'France' } }
    ],
}

const friendsReducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => action.id === u.id ? { ...u, followed: true } : u)
            }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => action.id === u.id ? { ...u, followed: false } : u)
            }
        case SET_USERS:
            return { ...state, users: [...state.users, ...action.users] }
        default: return state;
    }

}

export const followAC = (userID) => ({ type: FOLLOW, userID });
export const unfollowAC = (userID) => ({ type: UNFOLLOW, userID });
export const setUsersAC = () => ({ type: SET_USERS });


export default friendsReducer;
