let store = {
    _state: {
        Messages: {
            dialogItemsData : [
                {name: 'Henry Dinn' , id: 1 },
                {name: 'Michael Ross' , id: 2 },
                {name: 'Dick Irvin' , id: 3 },
                {name: 'Dafna Grass' , id: 4 }
            ],
            messagesData: [
                {message: 'Hey,how are you?', id: 1 },
                {message: 'Great! How are you?', id: 2, right: true},
                {message: 'Im fine! Go work!', id: 3},
                {message: 'Lets go!!!', id: 4, right: true},
            ],
            currentText: ''
        }
    },
    getState(){
        return this._state;
    },
    callSubscriber () {
        console.log('state changed');
    },
    
    subscribe (observer) {
        this.callSubscriber  = observer;
    },
  
    dispatch(action){
        if(action.type === 'SEND-MESSAGE'){
            let newMessage = {
                id: 5,
                message: this._state.Messages.currentText,
                right: true
            }
            this._state.Messages.messagesData.push(newMessage);
            this._state.Messages.currentText = '';
            this.callSubscriber(this._state);
        }
        else if(action.type === 'UPDATE-TEXT-MESSAGE'){
            this._state.Messages.currentText = action.newText;
            this.callSubscriber(this._state);
        }

    }
}



export default store;
