import React, { useEffect, useRef } from 'react'

import styles from './Messages.module.css'
import DialogItem from '../../DialogItem/DialogItem'
import MessageItem from '../../MessageItem/MessageItem'

import { reduxForm, Field } from 'redux-form';
import { createField } from '../../common/FormControls/FormControls';
import { required, maxLengthCreator } from '../../../utils/validators/validator';
import CustomScroll from 'react-custom-scroll';
import Scrollbars from "react-scrollbars-custom";
import ScrollbarHOC from '../../common/HOCScrollBar/HOCScrollbarCusctom';

const maxLength30 = maxLengthCreator(30);


const Messages = (props) => {
    
    let dialogItems = props.Messages.dialogItemsData.map((item, index) => <DialogItem key={index} name={item.name} id={item.id} />)

    
    return <div className={styles.messages}>
        <div className={styles.dialogItems}>
            <div className={styles.dialogSearch}></div>
            <div className={styles.dialogsList}>
                {dialogItems}

            </div>
        </div>
        {props.currentDialogId === 0
            ? 'chose dialog please'
            : <div className={styles.dialog}>
                <div className={styles.dialogHead}></div>
                <ScrollbarHOC children={<div className={styles.messagesFill}>{messageItems}</div>} autoHide ref={scrollBar} removeTrackYWhenNotUsed={true} style={{ width: "100%", height: "75%" }} />

                <div className={styles.formContainer}>
                    <AddMessageFormRedux onSubmit={props.sendMessage} />
                </div>
            </div>}

    </div>
}

const addMessageForm = (props) => {
    const onKeyHandler = (e) => {
        if (e.keyCode == 13 && e.shiftKey == false) {
            if (e.target.name == 'message') {
                e.preventDefault();
                props.handleSubmit();
            }
        }
    }
    return <form onKeyDown={onKeyHandler} onSubmit={props.handleSubmit}>
        {createField(null, 'message', [required], 'textarea', null, null)}

        {/* <button className={styles.buttonForm} type="submit">Send</button> */}
    </form>
}
const AddMessageFormRedux = reduxForm({ form: 'addMessageForm' })(addMessageForm);

const Dialog = (props) => {
    let messageItems = props.Messages.messagesData.map((item, index) => <MessageItem key={index} right={item.right} message={item.message} id={item.id} />)
    
    const scrollBar = useRef()

    useEffect(() => {
        scrollBar.current.scrollToBottom();
    }, [messageItems])
    return <>
        <div className={styles.dialogHead}></div>
        <ScrollbarHOC children={<div className={styles.messagesFill}>{messageItems}</div>} autoHide ref={scrollBar} removeTrackYWhenNotUsed={true} style={{ width: "100%", height: "75%" }} />
     </>
}
export default Messages;
